class Solution {
public:
    long long f(int i, int s, int P, bool T, bool U, const vector<int>& v, 
                map<tuple<int, int, int, int, int>, long long>& M) {
        if(i == v.size()) {
            if(!U) return 0;
            return (P % s == 0 ? 1LL : 0LL);
        }
        auto K = make_tuple(i, s, P, T, U);
        if(M.find(K) != M.end()) return M[K];

        long long r = 0;
        int L = (T ? v[i] : 9);
        for (int d = 0; d <= L; d++) {
            bool T2 = T && (d == L);
            if(!U && d == 0)
                r += f(i + 1, s, P, T2, false, v, M);
            else {
                int s2 = s + d;
                int P2 = (U ? P * d : d);
                r += f(i + 1, s2, P2, T2, true, v, M);
            }
        }
        M[K] = r;
        return r;
    }


    long long g(int a) {
        if(a < 1) return 0;
        vector<int> v;
        while(a > 0) {
            v.push_back(a % 10);
            a /= 10;
        }
        reverse(v.begin(), v.end());
        map<tuple<int, int, int, int, int>, long long> M;
        return f(0, 0, 1, true, false, v, M);
    }

    int beautifulNumbers(int a, int b) {
        long long c = g(b) - g(a - 1);
        return (int)c;
    }
};

