public class Solution {
    public int BeautifulNumbers(int l, int r) 
    {
        return Beautiful(r) - Beautiful(l - 1);
    }

    private int Beautiful(int x)
        {
            if (x < 1) return 0;  // no positive numbers below 1
            string s = x.ToString();
            //int n = s.Length;
            // memoization
            var memo = new Dictionary<(int pos, bool tight, bool started, bool hasZero, int sum, int prod), int>();
            return DP(0, true, false, false, 0, 1, s, memo);
        }

    private int DP(int pos, bool tight, bool started, bool hasZero, int sum, int prod, string s, Dictionary<(int, bool, bool, bool, int, int), int> memo)
        {
            if (pos == s.Length)
            {
                if (!started) return 0;
                if (hasZero) return 1;
                return (sum != 0 && prod % sum == 0) ? 1 : 0;
            }

            var key = (pos, tight, started, hasZero, sum, prod);
            if (memo.ContainsKey(key))
                return memo[key];

            int limit = tight ? s[pos] - '0' : 9;
            int res = 0;

            for (int d = 0; d <= limit; d++)
            {
                bool newTight = tight && (d == limit);
                if (!started)
                {
                    if (d == 0) res += DP(pos + 1, newTight, false, false, 0, 1, s, memo);
                    else res += DP(pos + 1, newTight, true, false, d, d, s, memo);
                }
                else
                {
                    if (d == 0) res += DP(pos + 1, newTight, true, true, sum, 0, s, memo);
                    else
                    {
                        if (hasZero) res += DP(pos + 1, newTight, true, true, sum + d, 0, s, memo);
                        else res += DP(pos + 1, newTight, true, false, sum + d, prod * d, s, memo);
                    }
                }
            }

            memo[key] = res;
            return res;
        }
