class Solution(object):
    def __init__(self):
        self.dp = {}

    def rec(self, pos, tight, started, hasZero, sum_digits, e2, e3, e5, e7, s):
        if pos == len(s):
            if not started:
                return 0
            if hasZero:
                return 1
            prod = 1
            for _ in range(e2):
                prod *= 2
            for _ in range(e3):
                prod *= 3
            for _ in range(e5):
                prod *= 5
            for _ in range(e7):
                prod *= 7
            return 1 if sum_digits != 0 and prod % sum_digits == 0 else 0

        key = (pos, tight, started, hasZero, sum_digits, e2, e3, e5, e7)
        if key in self.dp:
            return self.dp[key]

        res = 0
        limit = int(s[pos]) if tight else 9
        for d in range(limit + 1):
            ntight = tight and (d == limit)
            if not started:
                if d == 0:
                    res += self.rec(pos + 1, ntight, False, False, 0, 0, 0, 0, 0, s)
                else:
                    new_sum = d
                    new_e2 = new_e3 = new_e5 = new_e7 = 0
                    temp = d
                    while temp % 2 == 0:
                        new_e2 += 1
                        temp /= 2
                    while temp % 3 == 0:
                        new_e3 += 1
                        temp /= 3
                    while temp % 5 == 0:
                        new_e5 += 1
                        temp /= 5
                    while temp % 7 == 0:
                        new_e7 += 1
                        temp /= 7
                    res += self.rec(pos + 1, ntight, True, False, new_sum, new_e2, new_e3, new_e5, new_e7, s)
            else:
                if hasZero:
                    new_sum = sum_digits + d
                    res += self.rec(pos + 1, ntight, True, True, new_sum, 0, 0, 0, 0, s)
                else:
                    if d == 0:
                        new_sum = sum_digits
                        res += self.rec(pos + 1, ntight, True, True, new_sum, 0, 0, 0, 0, s)
                    else:
                        new_sum = sum_digits + d
                        new_e2, new_e3, new_e5, new_e7 = e2, e3, e5, e7
                        temp = d
                        while temp % 2 == 0:
                            new_e2 += 1
                            temp /= 2
                        while temp % 3 == 0:
                            new_e3 += 1
                            temp /= 3
                        while temp % 5 == 0:
                            new_e5 += 1
                            temp /= 5
                        while temp % 7 == 0:
                            new_e7 += 1
                            temp /= 7
                        res += self.rec(pos + 1, ntight, True, False, new_sum, new_e2, new_e3, new_e5, new_e7, s)

        self.dp[key] = res
        return res

    def countBeautiful(self, x):
        if x < 1:
            return 0
        s = str(x)
        self.dp.clear()
        return self.rec(0, True, False, False, 0, 0, 0, 0, 0, s)

    def beautifulNumbers(self, l, r):
        return int(self.countBeautiful(r) - self.countBeautiful(l - 1))

