class Solution {
    static String digits;
    static int n;
    static HashMap<String, Long> memo;

    public int beautifulNumbers(int l, int r) {
        return (int) (countBeautiful(r) - countBeautiful(l - 1));
    }

    long countBeautiful(long X) {
        digits = Long.toString(X);
        n = digits.length();
        memo = new HashMap<>();
        return countBeautifulHelper(0, true, false, false, 0, 1);
    }

    public static long countBeautifulHelper(int pos, boolean tight, boolean started, boolean hasZero, int sum, long product) {
        if (pos == n) {
            if (!started) return 0;
            if (hasZero) return 1;
            return (sum != 0 && product % sum == 0) ? 1 : 0;
        }

        // Build a key for memoization based on our state.
        String key = pos + "," + tight + "," + started + "," + hasZero + "," + sum + "," + product;
        if (memo.containsKey(key)) {
            return memo.get(key);
        }

        long count = 0;
        int limit = tight ? digits.charAt(pos) - '0' : 9;
        for (int d = 0; d <= limit; d++) {
            boolean newTight = tight && (d == limit);
            if (!started) {
                if (d == 0) {
                    // Still in leading zeros.
                    count += countBeautifulHelper(pos + 1, newTight, false, false, 0, 1);
                } else {
                    // Start the number with nonzero digit d.
                    count += countBeautifulHelper(pos + 1, newTight, true, false, d, d);
                }
            } else {
                if (d == 0) {
                    // Once we add a 0, mark hasZero true and product becomes 0.
                    count += countBeautifulHelper(pos + 1, newTight, true, true, sum, 0);
                } else {
                    // If we already encountered a zero, product remains 0.
                    if (hasZero) {
                        count += countBeautifulHelper(pos + 1, newTight, true, true, sum + d, 0);
                    } else {
                        // Multiply product by d and add d to sum.
                        count += countBeautifulHelper(pos + 1, newTight, true, false, sum + d, product * d);
                    }
                }
            }
        }

        memo.put(key, count);
        return count;
    }
}

