class Solution:
    def __init__(self):
        self.memo = {}
        self.num_str = ""

    def solve(self, idx, limit, has_started, has_zero, total_sum, pow2, pow3, pow5, pow7):
        if idx == len(self.num_str):
            if not has_started:
                return 0
            if has_zero:
                return 1
            product = (2 ** pow2) * (3 ** pow3) * (5 ** pow5) * (7 ** pow7)
            return 1 if total_sum != 0 and product % total_sum == 0 else 0

        state = (idx, limit, has_started, has_zero, total_sum, pow2, pow3, pow5, pow7)
        if state in self.memo:
            return self.memo[state]

        res = 0
        max_digit = int(self.num_str[idx]) if limit else 9

        for digit in range(max_digit + 1):
            new_limit = limit and (digit == max_digit)

            if not has_started:
                if digit == 0:
                    res += self.solve(idx + 1, new_limit, 0, 0, 0, 0, 0, 0, 0)
                else:
                    temp, count2, count3, count5, count7 = digit, 0, 0, 0, 0
                    while temp % 2 == 0:
                        count2 += 1
                        temp //= 2
                    while temp % 3 == 0:
                        count3 += 1
                        temp //= 3
                    while temp % 5 == 0:
                        count5 += 1
                        temp //= 5
                    while temp % 7 == 0:
                        count7 += 1
                        temp //= 7
                    res += self.solve(idx + 1, new_limit, 1, 0, digit, count2, count3, count5, count7)
            else:
                if has_zero:
                    res += self.solve(idx + 1, new_limit, 1, 1, total_sum + digit, 0, 0, 0, 0)
                else:
                    if digit == 0:
                        res += self.solve(idx + 1, new_limit, 1, 1, total_sum, 0, 0, 0, 0)
                    else:
                        temp, count2, count3, count5, count7 = digit, pow2, pow3, pow5, pow7
                        while temp % 2 == 0:
                            count2 += 1
                            temp //= 2
                        while temp % 3 == 0:
                            count3 += 1
                            temp //= 3
                        while temp % 5 == 0:
                            count5 += 1
                            temp //= 5
                        while temp % 7 == 0:
                            count7 += 1
                            temp //= 7
                        res += self.solve(idx + 1, new_limit, 1, 0, total_sum + digit, count2, count3, count5, count7)

        self.memo[state] = res
        return res

    def count_beautiful(self, num):
        if num < 1:
            return 0
        self.num_str = str(num)
        self.memo.clear()
        return self.solve(0, 1, 0, 0, 0, 0, 0, 0, 0)

    def beautifulNumbers(self, left, right):
        return self.count_beautiful(right) - self.count_beautiful(left - 1)

