class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def pre(n):
            digits = [int(d) for d in str(n)]

            @lru_cache(None)
            def helper(pos, tight, curr_sum, curr_product, started=False):
                if pos == len(digits):
                    return 1 if started and curr_sum > 0 and curr_product % curr_sum == 0 else 0

                result = 0
                limit = digits[pos] if tight else 9

                for d in range(limit + 1):
                    new_product = curr_product
                    if started:
                        new_product = 0 if d == 0 else curr_product * d
                    elif d > 0:
                        new_product = d

                    result += helper(
                        pos + 1,
                        tight and d == digits[pos],
                        curr_sum + d,
                        new_product,
                        started or d > 0
                    )
                return result

            return helper(0, True, 0, 1, False)

        r = pre(r)
        l = pre(l - 1)
        return r - l

