class Solution:
    # digit DP, time and space O(9L^2*9^L) = constant, when L = number of digits = log(r) < 10
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count_lt_eq(n: int) -> int:
            n_str = str(n)

            @cache
            def dp(
                pos: int,  # current digit index
                tight: bool,  # whether numbers prefix = n's prefix
                non_leading_zero: bool,  # whether placed a nonleadingzero digit
                has_zero: bool,  # whether a `0` is used 
                cur_sum: int,  # current digits sum
                cur_product: int,  # current digits product
            ) -> int:
                if pos == len(n_str):
                    if not non_leading_zero:
                        return 0
                    if has_zero:
                        return 1
                    return cur_product % cur_sum == 0
                res = 0
                limit = int(n_str[pos]) if tight else 9
                for d in range(limit + 1):
                    new_tight = tight and d == limit
                    if not non_leading_zero and d == 0:
                        res += dp(pos + 1, new_tight, False, False, 0, 1)
                    else:
                        new_has_zero = has_zero or d == 0
                        res += dp(
                            pos + 1, 
                            new_tight, 
                            True, 
                            new_has_zero, 
                            cur_sum + d, 
                            0 if new_has_zero else cur_product * d
                        )
                return res

            return dp(0, True, False, False, 0, 1)

        return count_lt_eq(r) - count_lt_eq(l - 1)

