class Solution {
    unordered_map<string, int> hash;

    string genKey(int pos, bool tight, bool started, bool hasZero, int sum, int prod) {
        return to_string(pos) + "_" + to_string(tight) + "_" + to_string(started) + "_" + to_string(hasZero)
               + "_" + to_string(sum) + "_" + to_string(prod);
    }

    int dp(int pos, bool tight, bool started, bool hasZero, int sum, int prod, string &s) {
        if (pos == s.size()) {
            if (!started) return 0;
            if (hasZero) return 1;
            return (prod % sum == 0) ? 1 : 0;
        }

        string key = genKey(pos, tight, started, hasZero, sum, prod);
        if(hash.count(key)) return hash[key];

        int ans = 0;
        int upperLimit = tight ? s[pos] - '0' : 9;
        for (int i = 0; i <= upperLimit; i++) {
            bool newStarted = started || (i != 0), newHasZero = hasZero;
            int newSum = sum, newProd = prod;
            if (newStarted) {
                if (started == false && i == 0) {
                    newStarted = false;
                } else {
                    newSum += i;
                    newProd = newProd * i;
                    newHasZero = i == 0 ? true : newHasZero;
                }
            }
            ans += dp(pos + 1, tight && (i == upperLimit), newStarted, newHasZero, newSum, newProd, s);
        }

        hash[key] = ans;
        return ans;
    }

    int countBeautiful(int x) {
        if(x < 1) return 0;
        string s = to_string(x);
        hash.clear();
        return dp(0, true, false, false, 0, 1, s);
    }

public:
    int beautifulNumbers(int l, int r) {
        return countBeautiful(r) - countBeautiful(l-1);
    }
};

