class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def cnt(x: int) -> int:
            if x < 1:
                return 0
            s = str(x)
            n = len(s)
            digits = list(map(int, s))
            @cache
            def dp(i, tight, started, ssum, pf):
                if i == n:
                    if not started or ssum == 0:
                        return 0
                    if pf == -1:
                        return 1
                    p2, p3, p5, p7 = pf
                    prod_mod = (pow(2, p2, ssum) * pow(3, p3, ssum) *
                                pow(5, p5, ssum) * pow(7, p7, ssum)) % ssum
                    return 1 if prod_mod == 0 else 0
                ans = 0
                lim = digits[i] if tight else 9
                for d in range(lim + 1):
                    ntight = tight and (d == lim)
                    if not started:
                        if d == 0:
                            ans += dp(i + 1, ntight, False, 0, (0, 0, 0, 0))
                        else:
                            nsum = d
                            npf = fac[d] if d != 0 else -1
                            ans += dp(i + 1, ntight, True, nsum, npf)
                    else:
                        nsum = ssum + d
                        if pf == -1 or d == 0:
                            npf = -1
                        else:
                            f2, f3, f5, f7 = pf
                            a2, a3, a5, a7 = fac[d]
                            npf = (f2 + a2, f3 + a3, f5 + a5, f7 + a7)
                        ans += dp(i + 1, ntight, True, nsum, npf)
                return ans
            return dp(0, True, False, 0, (0, 0, 0, 0))
        return cnt(r) - cnt(l - 1)

