class Solution {
public:
    int beautifulNumbers(int l, int r) {
        string digits;
        int len;

        vector<vector<vector<vector<map<int, int>>>>> dp(10, 
            vector<vector<vector<map<int, int>>>>(2, 
                vector<vector<map<int, int>>>(100, 
                    vector<map<int, int>>(2, map<int, int>()))
            )
        );
        bool considerTight = false;

        auto go = [&](auto&& go, string& s, int pos, bool tight, int sum, int prod, bool allPrefZero) -> int {
            if(pos == len) {
                if(tight && considerTight == false)
                    return 0;
                if(sum == 0)
                    return 0;
                // cout << s << " " << prod << " " << sum << " " << (prod % sum == 0) << "n";
                return prod % sum == 0;
            }
            if(dp[pos][tight][sum][allPrefZero].count(prod))
                return dp[pos][tight][sum][allPrefZero][prod];

            int ub = tight ? digits[pos] - '0' : 9;
            int cnt = 0;
            for(int d = 0; d <= ub; d++) {
                bool newAllPrefZero = allPrefZero && d == 0;
                int newProd = prod * (newAllPrefZero ? 1 : d);
                s.push_back('0' + d);
                cnt += go(go, s, pos + 1, tight && d == ub, sum + d, newProd, newAllPrefZero);
                s.pop_back();
            }
            return dp[pos][tight][sum][allPrefZero][prod] = cnt;
        };

        string s = "";
        digits = to_string(l);
        len = digits.size();
        int L = go(go, s, 0, true, 0, 1, true);

        considerTight = true;
        digits = to_string(r);
        len = digits.size();
        vector<vector<vector<vector<map<int, int>>>>> dp1(10, 
            vector<vector<vector<map<int, int>>>>(2, 
                vector<vector<map<int, int>>>(100, 
                    vector<map<int, int>>(2, map<int, int>()))
            )
        );
        dp = dp1;
        int R = go(go, s, 0, true, 0, 1, true);
        // cout << L << " " << R << "n";
        return R - L;
    }
};

