from functools import lru_cache

class Solution:

    @lru_cache(maxsize=None)
    def digit_dp(self, pos, tight, started, hasZero, s, p):

        if pos == len(self.digits):
            if not started:
                return 0
            if hasZero:
                return 1
            else:
                return 1 if (p % s == 0) else 0

        ans = 0

        up = self.digits[pos] if tight else 9

        for d in range(0, up + 1):

            new_tight = tight and (d == up)
            new_started = started or (d != 0)
            new_hasZero = hasZero or (new_started and d == 0)

            new_s = s
            new_p = p

            if new_started:
                new_s += d
                if new_hasZero:
                    new_p = 0
                else:
                    new_p *= d

            ans += self.digit_dp(pos + 1, new_tight, new_started, new_hasZero, new_s, new_p)

        return ans

    def count_valid(self, n):
        self.digit_dp.cache_clear()
        digits = list(map(int, str(n)))
        self.digits = digits
        return self.digit_dp(0, True, False, False, 0, 1)

    def beautifulNumbers(self, l: int, r: int) -> int:
        return self.count_valid(r) - self.count_valid(l - 1)

