def helper(l, r):
    def c(x):
        if x < 1: return 0
        s = str(x)
        @lru_cache(None)
        def f(i,t,st,hz,ss,p):
            if i == len(s):
                return 0 if not st else (1 if hz else (1 if ss and p%ss == 0 else 0))
            ans, lim = 0, (int(s[i]) if t else 9)
            for d in range(lim+1):
                nt = t and d == lim
                if not st:
                    ans += f(i+1, nt, d!=0, False, d, d if d else 1)
                else:
                    if hz: ans += f(i+1, nt, True, True, ss+d, p)
                    else:
                        if d == 0: ans += f(i+1, nt, True, True, ss, p)
                        else: ans += f(i+1, nt, True, False, ss+d, p*d)
            return ans
        return f(0, True, False, False, 0, 1)
    return c(r) - c(l-1)




class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        return helper(l,r)

