public class Solution
{
    public int BeautifulNumbers(int l, int r)
    {
        Dictionary<(int, bool, bool, int, int), long> cache = new();
        long high = helper(r.ToString(), 0, true, false, 0, 1);

        cache = new();
        long low = helper((l - 1).ToString(), 0, true, false, 0, 1);

        return (int)(high - low);

        long helper(string num, int index, bool isTight, bool isStarted, int digitSum, int digitProduct)
        {
            if (index == num.Length)
            {
                if (!isStarted)
                {
                    return 0;
                }

                return digitSum > 0 && digitProduct % digitSum == 0 ? 1 : 0;
            }

            var key = (index, isTight, isStarted, digitSum, digitProduct);
            if (cache.TryGetValue(key, out var val))
            {
                return val;
            }

            long result = 0;
            int currentDigit = num[index] - '0';
            int maxDigit = isTight ? currentDigit : 9;

            for (int i = 0; i <= maxDigit; i++)
            {
                bool newIsStarted = isStarted;
                int nextDigitSum = digitSum;
                int nextDigitProduct = digitProduct;

                if (!isStarted)
                {
                    if (i != 0)
                    {
                        newIsStarted = true;
                        nextDigitSum = digitSum + i;
                        nextDigitProduct = i;
                    }
                }
                else
                {
                    nextDigitSum = digitSum + i;
                    nextDigitProduct = digitProduct * i;
                }

                bool isNextTight = isTight && (i == currentDigit);
                result += helper(num, index + 1, isNextTight, newIsStarted, nextDigitSum, nextDigitProduct);
            }

            cache[key] = result;
            return result;
        }
    }
}

