    template <typename T1, typename... T2>
    void printIt(T1 t1, T2... t2) { cout << t1 << " , "; printIt(t2...); }
    // zx :)
    #define popcount __builtin_popcountll

private:
    i64 count(int n) {
        if (n < 1) {
            return 0;
        }
        string digits = to_string(n);
        unordered_map<string, i64> memo;
        return digitDP(0, true, false, 0, 1LL, digits, memo);
    }

    i64 digitDP(int pos, bool tight, bool started, int sum, i64 prod, const string &digits, unordered_map<string, i64> &memo) {
        if (pos == digits.size()) {
            return (started && sum > 0 && prod % sum == 0) ? 1 : 0;
        }
        string key = to_string(pos) + "," + to_string(tight) + "," + to_string(started) + "," + to_string(sum) + "," + to_string(prod);
        if (memo.count(key)) return memo[key];

        int limit = tight ? (digits[pos] - '0') : 9;
        i64 total = 0;

        for (int d = 0; d <= limit; d++) {
            bool nextTight = tight && (d == limit), nextStarted = started || (d != 0);
            int nextSum = sum;
            i64 nextProd = prod;

            if (nextStarted) {
                if (!started) {
                    nextSum = d;
                    nextProd = d;
                } 
                else {
                    nextSum += d;
                    nextProd *= d;
                }
            }
            total += digitDP(pos + 1, nextTight, nextStarted, nextSum, nextProd, digits, memo);
        }

        return memo[key] = total;
    }

public:
    int beautifulNumbers(int l, int r) {
        return count(r) - count(l - 1);
    }
};

