import java.util.Arrays;

class Solution {
  private void reset(int[][][][][] dp) {
    for (int[][][][] x : dp) for (int[][][] d : x) for (int[][] a : d) for (int[] b : a) Arrays.fill(b, -1);
  }

  private int solve(int ini, int index, int tight, int sumTillHere, int modTillHere, final int must, String s, final int n,
                    int[][][][][] dp) {
    if (index == n) {
      return sumTillHere == must && modTillHere == 0 ? 1 : 0;
    }
    if (dp[ini][tight][index][sumTillHere][modTillHere] != -1) return dp[ini][tight][index][sumTillHere][modTillHere];
    int limit = tight == 1 ? s.charAt(index) - '0' : 9;
    int res = 0;
    if (ini == 0) {
      for (int i = 0; i <= limit; ++i) {
        int nini = i == 0 ? 0 : 1;
        if (nini == 0) {
          res += solve(0, index + 1, 0, sumTillHere, modTillHere, must, s, n, dp);
        } else {
          int ntight = tight == 1 && i == limit ? 1 : 0;
          int nrem = (i);
          nrem %= must;
          res += solve(nini, index + 1, ntight, sumTillHere + i, nrem, must, s, n, dp);
        }
      }
    } else {
      for (int i = 0; i <= limit; ++i) {
        int ntight = tight == 1 && i == limit ? 1 : 0;
        int nrem = (modTillHere * i);
        nrem %= must;
        res += solve(ini, index + 1, ntight, sumTillHere + i, nrem, must, s, n, dp);
      }
    }

    return dp[ini][tight][index][sumTillHere][modTillHere] = res;
  }

  public int beautifulNumbers(int l, int r) {
    String right = Integer.toString(r);
    int adder = 0;
    int maxSum = 9 * (right.length());
    int[][][][][] dp = new int[2][2][right.length()][maxSum][maxSum];
    for (int i = 1; i <= maxSum; ++i) {
      reset(dp);
      /*
      ini , index, tight, sum, residue,
       */
      int here = solve(0, 0, 1, 0, 0, i, right, right.length(), dp);
      adder += here;
    }
    if (l == 1) return adder;
    l -= 1;
    String left = Integer.toString(l);
    int sub = 0;
    maxSum = 9 * (left.length());
    int[][][][][] dp2 = new int[2][2][left.length()][maxSum][maxSum];
    for (int i = 1; i <= maxSum; ++i) {
      reset(dp);
      sub += solve(0, 0, 1, 0, 0, i, left, left.length(), dp);
    }
    return adder - sub;
  }
}

