                                else if (p == 3)
                                    new3++;
                                else if (p == 5)
                                    new5++;
                                else if (p == 7)
                                    new7++;
                            }
                        }
                        p += (p == 2 ? 1 : 2);
                    }

                    ans += calculate(idx + 1, newBound, 1, 0, newTotal, new2,
                                     new3, new5, new7);
                }
            } else {
                if (containsZero) {
                    ans += calculate(idx + 1, newBound, 1, 1, total + digit, 0,
