from functools import lru_cache

class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count_beautiful(num):
            digits = list(map(int, str(num)))  # Convert number to digit array
            n = len(digits)

            @lru_cache(None)
            def dp(pos, tight, sum_digits, product_digits, has_nonzero):
                if pos == n:
                    return 1 if has_nonzero and product_digits % sum_digits == 0 else 0

                limit = digits[pos] if tight else 9
                total = 0

                for d in range(0, limit + 1):
                    new_sum = sum_digits + d
                    new_product = product_digits * d if has_nonzero else d  # Start product correctly
                    total += dp(pos + 1, tight and (d == limit), new_sum, new_product, has_nonzero or d > 0)

                return total

            return dp(0, True, 0, 1, False)  # Start with sum = 0, product = 1, and no nonzero digits

        return count_beautiful(r) - count_beautiful(l - 1)

