    }

    int solve(int pos, int sum, int product, int tight, int leadZero) {
        if (pos == s.size()) return check(sum, product);

        string key = to_string(pos) + "," + to_string(sum) + "," + to_string(product) + "," + to_string(tight);
        if (dp.find(key) != dp.end()) return dp[key];

        int limit = (tight) ? (s[pos] - '0') : 9;
        int count = 0;

        for (int digit = 0; digit <= limit; digit++) {
            int newSum = sum + digit;
            int newProduct = (leadZero && digit == 0) ? 1 : product * digit;
            count += solve(pos + 1, newSum, newProduct, tight && (digit == limit), leadZero && (digit == 0));
        }

        return dp[key] = count;
    }

    int countB(int num) {
        if (num == 0) return 0;
        dp.clear();
        s = to_string(num);
        return solve(0, 0, 1, 1, 1);
    }

    int beautifulNumbers(int l, int r) {
        return countB(r) - countB(l - 1);
    }
};

