    int dp[10][82][82][2]; 

    int solve(string &s, int i, int sum, int product, bool tight, int fixed_sum) {
        if (sum > fixed_sum) return 0;  

        if (i == s.size()) {
            return (sum == fixed_sum && product % fixed_sum == 0);
        }

        if (dp[i][sum][product][tight] != -1) return dp[i][sum][product][tight];

        int limit = tight ? (s[i] - '0') : 9;
        int ans = 0;

        for (int digit = 0; digit <= limit; digit++) {
            int new_product = (sum == 0 && digit == 0) ? 1 : (product * digit) % fixed_sum;  
            ans += solve(s, i + 1, sum + digit, new_product, tight && (digit == limit), fixed_sum);
        }

        return dp[i][sum][product][tight] = ans;
    }

    int countBeautiful(int x) {
        if (x < 1) return 0;  

        string s = to_string(x);
        int ans = 0;

        for (int fixed_sum = 1; fixed_sum <= 81; fixed_sum++) {
            memset(dp, -1, sizeof(dp)); 
            ans += solve(s, 0, 0, 1, 1, fixed_sum); 
        }

        return ans;
    }

    int beautifulNumbers(int l, int r) {
        return countBeautiful(r) - countBeautiful(l - 1);  
    }
};

