def create_key(position, is_tight, current_sum, product_modulo, has_leading_zero):
    return (position, is_tight, current_sum, product_modulo, has_leading_zero)

def convert(number):
    digits = []
    if number == 0:
        return [0]
    while number > 0:
        digits.append(number % 10)
        number //= 10
    digits.reverse()
    return digits

def f(position, is_tight, current_sum, product_modulo, has_leading_zero, digits, target_sum, cache):
    if position == len(digits):
        return 1 if (current_sum == target_sum and product_modulo == 0 and not has_leading_zero) else 0

    if not has_leading_zero:
        remaining_digits = len(digits) - position
        max_addition = 9 * remaining_digits
        if current_sum > target_sum or current_sum + max_addition < target_sum:
            return 0

    key = create_key(position, is_tight, current_sum, product_modulo, has_leading_zero)
    if key in cache:
        return cache[key]

    result = 0
    upper_limit = digits[position] if is_tight else 9

    for digit in range(0, upper_limit + 1):
        new_is_tight = is_tight and (digit == upper_limit)
        new_has_leading_zero = has_leading_zero and (digit == 0)
        new_sum = current_sum
        new_product = product_modulo

        if has_leading_zero:
            if digit != 0:
                new_sum = digit
                new_product = digit % target_sum
                new_has_leading_zero = False
        else:
            new_sum += digit
            new_product = (product_modulo * digit) % target_sum

        if not new_has_leading_zero and new_sum > target_sum:
            continue

        result += f(position + 1, new_is_tight, new_sum, new_product, new_has_leading_zero, digits, target_sum, cache)

    cache[key] = result
    return result

def count_beautiful_numbers(number):
    if number < 0:
        return 0
    digits = convert(number)
    tot = 0

    for target_sum in range(1, 9 * len(digits) + 1):
        tot += f(0, True, 0, 1, True, digits, target_sum, {})

    return tot

class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        lower_count = count_beautiful_numbers(l - 1) if l > 0 else 0
        upper_count = count_beautiful_numbers(r)
        return upper_count - lower_count if upper_count - lower_count >= 0 else -1

