        if (!started) {
            if (d == 0) {
            } else {
                nstarted = 1;
                nsum = d;
                nprod = d;
            }
        } else {
            nsum += d;
            nprod *= d;
        }

        res += dp(pos + 1, ntight, nstarted, nsum, nprod);
