class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:

        def cb(x):
            digits = list(map(int, str(x)))
            n = len(digits)

            @lru_cache(maxsize=None)
            def dp(pos, tight, started, s, prod, has_zero):

                if pos == n:
                    if not started:
                        return 0
                    if has_zero:
                        return 1
                    return 1 if prod % s == 0 else 0

                total = 0
                limit = digits[pos] if tight else 9
                for d in range(limit + 1):
                    new_tight = tight and (d == limit)
                    if not started:
                        if d == 0:
                            total += dp(pos + 1, new_tight, False, 0, 1, False)
                        else:
                            total += dp(pos + 1, new_tight, True, d, d, False)
                    else:
                        new_s = s + d
                        if has_zero or d == 0:
                            total += dp(pos + 1, new_tight, True, new_s, 0, True)
                        else:
                            total += dp(pos + 1, new_tight, True, new_s, prod * d, False)
                return total

            return dp(0, True, False, 0, 1, False)

        result = cb(r) - cb(l - 1)
        return result

