class Solution {
public:
    int dp[83][83][10][2][2];

    int f(string& s,int rem, int sum,int p=1,int i=0,bool high = 1, bool zero = 1) {

        if(rem < 0) {
            return 0;
        }

        if(i == s.size()) {
            if(rem == 0 && p%sum == 0) {
                return 1;
            }
            return 0;
        }

        if(dp[rem][p][i][high][zero] != -1) {
            return dp[rem][p][i][high][zero];
        }

        int start = 0;
        int end = (high == 1 ? s[i] - '0' : 9);
        int cnt = 0;

        for(int j = start; j <= end;j++) {
            bool newHigh = (high && (j==end));
            bool newZero = (zero && j==0);

            int p1 = newZero ? 1 : (p*j)%sum;

            cnt += f(s,rem-j,sum,p1,i+1,newHigh, newZero);
        }
        return dp[rem][p][i][high][zero] = cnt;
    }

    int beautifulNumbers(int l, int r) {
        string s = to_string(r);
        string t = to_string(l-1);
        int ans = 0;

        for(int i=1;i<=81;i++) {

            memset(dp, -1,sizeof(dp));
            ans += f(s,i,i);

            memset(dp, -1,sizeof(dp));
            ans -= f(t,i,i);
        }
        return ans;
    }
};

