class Solution {
public:
    int d[11], top, f[11][91][91];
    void split(int x) {
        top = 0;
        for(; x; x /= 10) d[++top] = x % 10;
    }
    int dfs(int u, int sum, int rem, int mul, bool is0, bool lim) {
        if(u == 0) return rem == 0 && mul == 0;
        if(!is0 && !lim && f[u][rem][mul] != -1) return f[u][rem][mul];
        int now = 0, k = lim ? d[u] : 9;
        for(int c = 0; c <= k; ++c) {
            if(c > rem) break;
            int nxt_rem = rem - c;
            int nxt_mul = ((is0 && c == 0) ? 1 : (mul * c)) % sum;
            now += dfs(u - 1, sum, nxt_rem, nxt_mul, is0 && c == 0, lim && c == k);
        }
        if(!is0 && !lim) f[u][rem][mul] = now;
        return now;
    }
    int calc(int x, int sum) {
        split(x);
        for(int i = 0; i <= top; ++i) {
            for(int j = 0; j <= sum; ++j) {
                for(int k = 0; k <= sum; ++k) {
                    f[i][j][k] = -1;
                }
            }
        }
        return dfs(top, sum, sum, 1, true, true);
    }
    int beautifulNumbers(int l, int r) {
        int ans = 0;
        for(int i = 1; i <= 81; ++i) {
            ans += calc(r, i) - calc(l - 1, i);
        }
        return ans;
    }
};

