package main

import "strconv"

// https://space.bilibili.com/206214
func beautifulNumbers(low int, high int) (ans int) {
	lowS := strconv.Itoa(low)
	highS := strconv.Itoa(high)
	n := len(highS)
	diffLH := n - len(lowS)

	for tar := 1; tar <= n*9; tar++ {
		memo := make([][][]int, n)
		for i := range memo {
			memo[i] = make([][]int, tar)
			for j := range memo[i] {
				memo[i][j] = make([]int, tar+1)
				for k := range memo[i][j] {
					memo[i][j][k] = -1
				}
			}
		}

		var dfs func(int, int, int, bool, bool) int
		dfs = func(i, mul, s int, limitLow, limitHigh bool) (res int) {
			if s > tar {
				return
			}
			if i == n {
				if s != tar || mul%s > 0 {
					return
				}
				return 1
			}
			if !limitLow && !limitHigh {
				dv := &memo[i][mul][s]
				if *dv >= 0 {
					return *dv
				}
				defer func() { *dv = res }()
			}

			lo := 0
			if limitLow && i >= diffLH {
				lo = int(lowS[i-diffLH] - '0')
			}
			hi := 9
			if limitHigh {
				hi = int(highS[i] - '0')
			}

			d := lo
			if limitLow && i < diffLH {
				res = dfs(i+1, mul, 0, true, false)
				d++
			}
			for ; d <= hi; d++ {
				res += dfs(i+1, mul*d%tar, s+d, limitLow && d == lo, limitHigh && d == hi)
			}
			return
		}
		res := dfs(0, 1%tar, 0, true, true)
		ans += res
	}

	return
}

