import string
from sortedcontainers import SortedList
from collections import defaultdict, deque, Counter
from bisect import bisect_left, bisect_right
from typing import List, Set, Tuple, Optional
from itertools import pairwise, permutations, combinations, groupby
from heapq import heappush, heappop
from random import shuffle
from functools import cmp_to_key, lru_cache
from fractions import Fraction
from math import gcd, lcm

record = {}


def core(n: int):
    if n in record.keys():
        return record[n]

    a = list(map(int, str(n)))
    m = len(a)
    f = [[defaultdict(int), defaultdict(int)] for _ in range(m)]
    for i in range(1, a[0]):
        f[0][0][i, i] = 1
    f[0][1][a[0], a[0]] = 1
    for i in range(m - 1):
        for (s, m), val in f[i][0].items():
            for d in range(10):
                f[i + 1][0][s + d, m * d] += val
        for (s, m), val in f[i][1].items():
            for d in range(a[i + 1]):
                f[i + 1][0][s + d, m * d] += val
            d = a[i + 1]
            f[i + 1][1][s + d, m * d] += val
    ans = 0
    for mp in f[-1]:
        for (s, m), val in mp.items():
            if m % s == 0:
                ans += val
    record[n] = ans
    return ans

class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def solve(n) -> int:
            if n == 0:
                return 0
            s = str(n)
            m = len(s)
            ans = 0
            for i in range(1, m):
                ans += core(10 ** i - 1)
            ans += core(n)
            return ans

        return solve(r) - solve(l - 1)

