constexpr int N = 100;
constexpr int MOD = 1e9 + 7;
constexpr int ALPHA_SIZE = 10, MIN_ALPHA = '0';
long long dp[N][ALPHA_SIZE][2][2];
long long solve(const vector<int>& digits) {
    fill(&dp[0][0][0][0], &dp[N - 1][ALPHA_SIZE - 1][1][1] + 1, -1LL);
    int n = digits.size();
    const auto dfs = [&](const auto& dfs, int i, int c, bool eq,
                         bool nonzero) -> long long {
        if (i == n) {
            return nonzero;
        }
        if (dp[i][c][eq][nonzero] != -1) {
            return dp[i][c][eq][nonzero];
        }
        int lim = eq ? digits[i] : ALPHA_SIZE - 1;
        long long res = 0;
        for (int d = 0; d <= lim; ++d) {
            res += dfs(dfs, i + 1, d, eq && d == lim, nonzero || d > 0);
        }
        return dp[i][c][eq][nonzero] = res;
    };
    return dfs(dfs, 0, 0, true, false);
}
vector<int> get_digits(const string& x) {
    vector<int> digits;
    for (auto c : x) {
        digits.push_back(c - MIN_ALPHA);
    }
    return digits;
}
vector<int> get_digits(long long x) {
    vector<int> digits;
    while (x > 0) {
        digits.push_back(x % ALPHA_SIZE);
        x /= ALPHA_SIZE;
    }
    reverse(digits.begin(), digits.end());
    return digits;
}
vector<int> subtract_one(vector<int> digits) {
    int n = digits.size(), i = n - 1;
    for (; i >= 0; --i) {
        if (digits[i] != 0) {
            break;
        }
    }
    --digits[i++];
    for (; i < n; ++i) {
        digits[i] = ALPHA_SIZE - 1;
    }
    return digits;
}
template <typename T> long long perform_dp(const T& high, const T& low) {
    auto parsed_high = get_digits(high), parsed_low = get_digits(low);
    auto high_count = solve(parsed_high);
    auto need_low = any_of(parsed_low.begin(), parsed_low.end(),
                           [&](auto x) { return x != MIN_ALPHA; });
    auto low_count = need_low ? solve(subtract_one(parsed_low)) : 0;
    return high_count - low_count;
}
vector<int> get_digits_ll(long long x) {
    vector<int> digits;
    while (x > 0) {
        digits.push_back(x % 10);
        x /= 10;
    }
    if (digits.empty())
        digits.push_back(0);
    reverse(digits.begin(), digits.end());
    return digits;
}
struct state_hash {
    size_t operator()(const array<int, 6>& s) const {
        size_t res = 0;
        for (auto v : s) {
            res = res * 31u + (unsigned)v;
        }
        return res;
    }
};
unordered_map<array<int, 6>, long long, state_hash> memo;
long long dp_dfs(int pos, int tight, int started, int has_zero, int sum,
                 int prod, const vector<int>& digits) {
    if (pos == digits.size()) {
        if (!started)
            return 0;
        if (has_zero)
            return 1;
        return (prod % sum == 0) ? 1 : 0;
    }
    array<int, 6> st = {pos, tight, started, has_zero, sum, prod};
    if (memo.find(st) != memo.end())
        return memo[st];
    int lim = tight ? digits[pos] : 9;
    long long res = 0;
    for (int d = 0; d <= lim; d++) {
        int ntight = (tight && d == lim);
        int nstarted = started || (d > 0);
        int nhas_zero = has_zero;
        int nsum = sum;
        int nprod = prod;
        if (!started) {
            if (d == 0) {
                res += dp_dfs(pos + 1, ntight, nstarted, nhas_zero, nsum, nprod,
                              digits);
            } else {
                nsum = d;
                nprod = d;
                res += dp_dfs(pos + 1, ntight, 1, 0, nsum, nprod, digits);
            }
        } else {
            if (has_zero) {
                res += dp_dfs(pos + 1, ntight, 1, 1, 0, 0, digits);
            } else {
                if (d == 0) {
                    nhas_zero = 1;
                    res += dp_dfs(pos + 1, ntight, 1, 1, 0, 0, digits);
                } else {
                    nsum = sum + d;
                    nprod = prod * d;
                    res += dp_dfs(pos + 1, ntight, 1, 0, nsum, nprod, digits);
                }
            }
        }
    }
    memo[st] = res;
    return res;
}
long long count_beautiful(long long x) {
    if (x <= 0)
        return 0;
    vector<int> digits = get_digits_ll(x);
    memo.clear();
    return dp_dfs(0, 1, 0, 0, 0, 1, digits);
}
class Solution {
public:
    int beautifulNumbers(int l, int r) {
        long long res = count_beautiful(r) - count_beautiful(l - 1);
        return res;
    }
};

