const int N = 90;
int dp[10][N][N][N];
int len, val[10];

auto init = [](){
    memset(dp, -1, sizeof(dp));
    return 0;
}();

int solve(int p, bool limit, int s, int f, int m){
    if(p < 0){
        if(s == m && f == 0){
            return 1;
        }
        return 0;
    }
    if(s > m){
        return 0;
    }
    if(!limit && f != -1 && dp[p][s][f][m] != -1){
        return dp[p][s][f][m];
    }
    int ret = 0;
    for(int i=0; i<10; ++i){
        if(limit && i > val[p])  continue;
        int g;
        if(f == -1){
            if(i == 0)  g = -1;
            else  g = i % m;
        } else {
            g = f * i % m;
        }
        ret += solve(p - 1, limit && i == val[p], s + i, g, m);
    }
    if(!limit && f != -1){
        dp[p][s][f][m] = ret;
    }
    return ret;
}

int query(int v){
    if(v == 0)  return 0;
    len = 0;
    while(v > 0){
        val[len++] = v % 10;
        v /= 10;
    }
    int ans = 0;
    for(int i=1; i<=81; ++i){
        ans += solve(len - 1, 1, 0, -1, i);
    }
    return ans;
}

class Solution {
public:
    int beautifulNumbers(int l, int r) {
        return query(r) - query(l - 1);
    }
};

