import java.util.Arrays;

class Solution {
    public int beautifulNumbers(int l, int r) {
        int res=0;
        char[] sr = String.valueOf(r).toCharArray();
        char[]sl1 = String.valueOf(l-1).toCharArray();
        for(int sum = 1; sum<=81 && sum<=9*sr.length; ++sum){
            res += help(sr, r, sum) - help(sl1, l-1, sum);
        }

        return res;
    }
    int N = 82;
    // i  j k
    int[][][] memo = new int[10][N][N];
    int help(char[]sl, int l, int sum){
        if(l==0)return 0;
        for (int[][] m1 : memo) {
            for (int[] m2 : m1) {
                Arrays.fill(m2, -1);
            }
        }

        return dp(sl,l, sum, 0, sum, true, true);
    }
    int dp(char[]sl, int l, int sum, int index, int mul,
           boolean first, boolean limit){
        int n = sl.length;
        // mul
        if(index==n){
            if(mul==1 && sum==0){
                return 1;
            }
            return 0;
        }
        // 0
        if(sum==0){
            return 1;
        }
        if(!first && !limit &&memo[index][sum][mul]!=-1)return memo[index][sum][mul];

        int res=0;

        int up = limit ? sl[index]-'0' :9;

        for(int i=0;i<=up && i<=sum;++i){
            int nMul = mul;
            if(i==0){
                if(!first)nMul = 1;
            }else{
                if(nMul>1)nMul /= gcd(mul, i);
            }

            res += dp(sl, l,sum-i,index+1, nMul,
                    first&&i==0,limit&&i==up);

        }

        return memo[index][sum][mul] = res;
    }

    int gcd(int a, int b){
        while(b!=0){
            int temp = b;
            b = a % b;
            a = temp;
        }
        return a;
    }
}

