class Solution {
public:
    int beautifulNumbers(int L, int R) {
        int ret = 0;
        for (int s = 1; s <= 81; ++s) {
            auto solve = [&](int n) {
                if (n == 0) return 0;
                vector<int> digit;
                for (int t = n; t; t /= 10) digit.push_back(t % 10);
                reverse(digit.begin(), digit.end());
                int m = digit.size();
                for (int i = 0; i < m; ++i) {
                  for (int j = 0; j < 2; ++j) {
                    for (int u = 0; u <= s; ++u) {
                      for (int v = 0; v < s; ++v) {
                        dp[i][j][u][v] = -1;
                      }
                    }
                  }
                }
                // auto dp = vect<int>(-1, m, 2, s + 1, s);
                function<int(int, int, int, int)> rec =
                    [&](int pos, int tight, int sum, int r) -> int {
                      // trace(pos, tight, sum, r, m, digit);
                    if ((m - pos) * 9 + sum < s) return 0;
                    if (pos == m) return sum == s && r == 0;
                    int& ret = dp[pos][tight][sum][r];
                    // trace(pos, ret);
                    if (ret >= 0) return ret;
                    ret = 0;
                    for (int x = 0; x < 10; ++x) {
                        if (tight && x > digit[pos]) continue;
                        int nsum = sum + x;
                        int nr = x > 0 ? (r * x) % s : (sum == 0 ? r : 0);
                        int ntight = tight && (x == digit[pos]);
                        // trace(x, nsum, nr, ntight);
                        if (nsum > s) continue;
                        // trace(x, nsum, nr, ntight);
                        ret += rec(pos + 1, ntight, nsum, nr);
                    }
                    return ret;
                };
                int ret = rec(0, 1, 0, 1 % s);
                // if (ret) trace(n, s, digit, ret);
                return ret;
            };
            ret += solve(R) - solve(L - 1);
        }
        return ret;
    }
};

