unordered_map<int, unordered_map<int, Mp>> jyh;
class Solution {
    int Sum(const Mp& last) {
        int res = 0;
        for (const auto& [he, mp] : last) {
            for (const auto& [ji, x] : mp) {
                if (he && ji % he == 0) {
                    res += x;
                }
            }
        }
