        if (res.size()) {
            return res;
        }
        int base = pow(10, wei) + 0.5;
        int cur = n / base;
        Mp last = Btf(n % base, wei - 1);
        // cout << n << ',' << wei << ',' << base << ',' << cur << endl;
        for (const auto& [he, mp] : last) {
            for (const auto& [ji, x] : mp) {
                res[he + cur][ji * cur] += x;
            }
        }
        if (cur > 0) {
            Mp last9 = Btf(base - 1, wei - 1);
            for (const auto& [he, mp] : last9) {
                for (const auto& [ji, x] : mp) {
                    for (int i = 0; i < cur; i++) {
                        res[he + i][ji * i] += x;
