    int Beautiful(int n) {
        int base = 1;
        int wei = 0;
        int res = 0;
        while (true) {
            if (base * 10 > n) {
                res += Sum(Btf(n, wei)) - Sum(Btf(base - 1, wei));
                return res;
            }
            res += Sum(Btf(base * 10 - 1, wei)) - Sum(Btf(base - 1, wei));
            wei++;
            base *= 10;
        }
    }

public:
    int beautifulNumbers(int l, int r) {
        return Beautiful(r) - Beautiful(l - 1);
    }
};

