class Solution {
public:
  int top, num[12];
  int m;
  int dp[12][82][82][2][2];
  bool vis[12][82][82][2][2];
  int dfs(int now, int x, int s, bool up, bool zz) {
    if (now == 0) {
      return x == 0 && s == 0;
    }
    if (vis[now][x][s][up][zz])
      return dp[now][x][s][up][zz];
    vis[now][x][s][up][zz] = true;
    int &ans = dp[now][x][s][up][zz];
    ans = 0;
    int lim = up ? num[now] : 9;
    for (int j = 0; j <= lim && j <= s; ++j) {
      ans = (ans + dfs(now - 1, (zz || j) ? x * j % m : x, s - j,
                       up && j == lim, zz || (j != 0)));
    }
    return ans;
  }
  int sol(int xx) {
    int x = xx;
    int ans = 0;
    top = 0;
    while (x) {
      num[++top] = x % 10;
      x /= 10;
    }
    for (int i = 1; i <= 81; ++i) {
      m = i;
      memset(vis, false, sizeof(vis));
      ans = (ans + dfs(top, 1, i, 1, 0));
    }
    return ans;
  }
  int beautifulNumbers(int l, int r) { return sol(r) - sol(l - 1); }
};

