class Solution {
public:
    int f(int x){
        string s = to_string(x);
        unordered_map<string, long long> memo;
        function<long long(int, int, int, int, int)> dfs = [&](int pos, int tight, int started, int sum, int prod) -> long long {
            if(pos == s.size()){
                if(!started) return 0;
                return (prod % sum == 0) ? 1LL : 0LL;
            }
            string key = to_string(pos) + "_" + to_string(tight) + "_" + to_string(started) + "_" + to_string(sum) + "_" + to_string(prod);
            if(memo.count(key))
                return memo[key];

            long long res = 0;
            int up = tight ? (s[pos] - '0') : 9;
            for(int dig = 0; dig <= up; dig++){
                int ntight = tight;
                if(tight && dig < up) ntight = 0;
                int nstarted = started;
                int nsum = sum;
                int nprod = prod;
                if(!started && dig == 0){
                } else if(!started && dig > 0){
                    nstarted = 1;
                    nsum = dig;
                    nprod = dig;
                } else {
                    nsum = sum + dig;
                    nprod = prod * dig;
                }
                res += dfs(pos + 1, ntight, nstarted, nsum, nprod);
            }
            memo[key] = res;
            return res;
        };

        return dfs(0, 1, 0, 0, 1);
    }
    int beautifulNumbers(int l, int r) {
        return f(r)-f(l-1);
    }
};

