        @cache
        def dfs(i: int, limit_low: bool, limit_high: bool, pre: bool, prod: int, s: int) -> int:
            if i == n:
                return 1 if (not pre and prod % s == 0) else 0

            #  i  lo  hi
            #  for  lo  hi
            lo = int(low[i]) if limit_low else 0
            hi = int(high[i]) if limit_high else 9

            res = 0
            for d in range(lo, hi + 1):
                res += dfs(i + 1, limit_low and d == lo, limit_high and d == hi, pre and not d, 1 if (pre and not d) else prod * d, s + d)
            return res

        return dfs(0, True, True, diff, 1, 0)

