import java.util.HashMap;
import java.util.Map;

class Solution {
    public int beautifulNumbers(int l, int r) {
        int ans = dfs(String.valueOf(r).toCharArray(), 0, true, false, 1, 0);
        memo.clear();
        ans -= dfs(String.valueOf(l - 1).toCharArray(), 0, true, false, 1, 0);
        return ans;
    }

    Map<Long, Integer> memo = new HashMap<>();

    int dfs(char[] s, int i, boolean isLimit, boolean isNum, long mul, int sum) {
        if (i == s.length) {
            if (isNum && mul % sum == 0) {
                int aa = 0;
            }
            return isNum && mul % sum == 0 ? 1 : 0;
        }
        long mask = mul << 15 | (long) sum << 5 | i;
        if (!isLimit && isNum && memo.containsKey(mask)) {
            return memo.get(mask);
        }
        int res = 0;
        if (!isNum) {
            res += dfs(s, i + 1, false, false, 1, 0);
        }
        for (int d = isNum ? 0 : 1, up = isLimit ? s[i] - '0' : 9; d <= up; d++) {
            res += dfs(s, i + 1, isLimit && d == up, true, mul * d, sum + d);
        }
        if (!isLimit && isNum) {
            memo.put(mask, res);
        }
        return res;
    }
}

