class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        ll = [int(c) for c in str(l-1)]
        lr = [int(c) for c in str(r)]

        def deal_value(in_value) :
            if in_value == 0 :
                return in_value
            out_value = 1
            while in_value % 2 == 0 and not out_value % 64 == 0 :
                in_value /= 2
                out_value *= 2
            while in_value % 3 == 0 and not out_value % 81 == 0 :
                in_value /= 3
                out_value *= 3
            while in_value % 5 == 0 and not out_value % 25 == 0 :
                in_value /= 5
                out_value *= 5
            while in_value % 7 == 0 and not out_value % 49 == 0 :
                in_value /= 7
                out_value *= 7
            return out_value

        # case=0 <=ll case=1<=lr
        @functools.lru_cache(None)
        def solve(case=0, lastm=1, lasts=0, eq=True, pt=0, isstart=True) :
            listt = ll if case == 0 else lr
            if pt == len(listt) :
                if isstart :
                    return 0
                # print(case, lastm, lasts, eq, pt)
                # print(1 if lastm % lasts == 0 else 0, '!')
                return 1 if lastm % lasts == 0 else 0

            minv = 0 
            maxv = 9 if not eq else listt[pt]
            to_ret = 0
            for vnow in range(minv, maxv+1) :
                neq = eq and vnow == listt[pt]
                if isstart and vnow == 0 :
                    newm, news = lastm, lasts
                    new_start=True
                else :
                    newm, news = deal_value(lastm*vnow), lasts+vnow
                    new_start=False
                to_ret += solve(case, newm, news, neq, pt+1, new_start)
            # print(case, lastm, lasts, eq, pt)
            # print(to_ret)
            return to_ret


        v1 = solve(case=0)
        v2 = solve(case=1)
        return v2-v1

