class Solution {
  public:
    unordered_map<uint64_t, int> memo;
    string s;

    // digit dp
    int dp(uint8_t pos, bool tight, bool started, bool zerofound, uint32_t sum,
           uint32_t prod) {
        if (pos == s.size()) {
            if (!started) {
                return 0;
            }
            if (zerofound) {
                return 1;
            }
            return (prod % sum == 0 ? 1 : 0);
        }

        auto key = (static_cast<uint64_t>(pos)) | // pos max 9, use 4 bits
                   (static_cast<uint64_t>(tight) << 4) |
                   (static_cast<uint64_t>(started) << 5) |
                   (static_cast<uint64_t>(zerofound) << 6) |
                   (static_cast<uint64_t>(sum) << 7) | // sum max 81, use 7 bits
                   (static_cast<uint64_t>(prod) << 16);

        if (auto it = memo.find(key); it != memo.end()) {
            return it->second;
        }

        int ans = 0;
        int limit = (tight ? s[pos] - '0' : 9);
        for (int d = 0; d <= limit; d++) {
            bool ntight = (tight && d == limit);
            if (!started) {
                if (d == 0) {
                    ans += dp(pos + 1, ntight, false, false, 0, 1);
                } else {
                    ans += dp(pos + 1, ntight, true, false, d, d);
                }
            } else {
                if (!zerofound) {
                    if (d == 0) {
                        ans += dp(pos + 1, ntight, true, true, 0, 0);
                    } else {
                        ans +=
                            dp(pos + 1, ntight, true, false, sum + d, prod * d);
                    }
                } else {
                    ans += dp(pos + 1, ntight, true, true, 0, 0);
                }
            }
        }

        memo[key] = ans;
        return ans;
    }

    int check(long long x) {
        s = to_string(x);
        memo.clear();
        return dp(0, true, false, false, 0, 1);
    }

    int beautifulNumbers(int l, int r) { return check(r) - check(l - 1); }
};

