ll dfs(int pos,int isu,int lead,int ndsum,int pro,int sum){
	if(pos<0 && ndsum==sum && pro==0) return 1;
    else if(pos<0) return 0;
	if(!isu&&dp[pos][pro][sum]!=-1&&!lead) return dp[pos][pro][sum];

	int up=isu ? a[pos]:9;
	ll ans=0;
	for(int i=0;i<=up;i++){
        int x;
        if(lead && i==0) x=pro;
        else x=pro*i%ndsum;
		ans+=dfs(pos-1,isu&&i==up,lead&&i==0,ndsum,x,sum+i);
	}
	if(!isu&&!lead) dp[pos][pro][sum]=ans;
	return ans;
}

