class Solution {
public:
    int beautifulNumbers(int l, int r) {
        static pair<int, int> f[9][2][2][81][81];
        int n, a[9];
        int m;
        static int timestamp = 0;
        auto dp = [&](this auto& dp, int i, int limit, int pos, int r, int s) -> int {
            if (s < 0) return 0;
            if (i == n) return r == 0 && s == 0;
            auto &[ret, t] = f[i][limit][pos][r][s];
            if (t == timestamp) return ret;
            ret = 0;
            int D = limit ? a[i] : 9;
            for (int d = 0; d <= D && s >= 0; ++d) {
                ret += dp(i + 1, limit && d == D, pos || d > 0, pos ? r * d % m : d % m, s);
                --s;
            }
            t = timestamp;
            return ret;
        };
        auto gao = [&](int x) {
            if (x == 0) return 0;
            n = 0;
            for (; x > 0; x /= 10) a[n++] = x % 10;
            reverse(a, a + n);
            ++timestamp;
            return dp(0, 1, 0, 0, m);
        };
        int ans = 0;
        for (int s = 1; s <= 81; ++s) {
            m = s;
            ans += gao(r);
            ans -= gao(l - 1);
        }
        return ans;
    }
};

