class Solution {
    public:
        int beautifulNumbers(int l, int r) {

            auto calc = [&](int N) {
                string s = to_string(N);
                int n = (int) s.length();

                int M = n * 9;

                auto reset = [&](int m, int t) {
                    for (int k = 0; k < 2; ++k) {
                        for (int i = 0; i <= m; ++i) {
                            for (int j = 0; j <= m; ++j) {
                                dp[t][k][i][j] = 0;
                            }
                        }
                    }
                };

                long long res = 0;
                for (int m = 1; m <= M; ++m) {

                    reset(m, 0);
                    int t = 0;
                    for (int i = 0; i < n; ++i, t ^= 1) {
                        int d = s[i] - '0';

                        int from = t;
                        int to = from ^ 1;

                        auto& cur = dp[from];
                        auto& nxt = dp[to];
                        reset(m, to);

                        for (int j = 0; j < 2; ++j) {
                            for (int k = 0; k <= m; ++k) {
                                for (int l = 0; l < m; ++l) {
                                    if (cur[j][k][l] == 0) {
                                        continue;
                                    }

                                    int ll = 0, rr = j == 0 ? d : 9;
                                    for (int dd = ll; dd <= rr && dd + k <= m; ++dd) {
                                        int nj = j;
                                        if (dd < d) {
                                            nj = 1;
                                        }

                                        int nk = k + dd;
                                        int nl = l * dd % m;
                                        nxt[nj][nk][nl] += cur[j][k][l];
                                    }
                                }
                            }
                        }


                        for (int dd = 1; dd <= (i == 0 ? d : 9) && dd <= m; ++dd) {
                            int nj = 0;
                            if (i > 0 || dd < d) {
                                nj = 1;
                            }
                            nxt[nj][dd][dd % m] += 1;
                        }
                    }

                    res += dp[t][1][m][0];
                }
                return res;
            };


            auto res = calc(r + 1) - calc(l);
            return res;
        }
    };

