class Solution {
public:
    int beautifulNumbers(int l, int r) {
        auto query = [&] (int p) -> int {
            if(p == 0) return 0;
            vector<int> digits;
            int tmp = p;
            while(tmp) {
                digits.push_back(tmp % 10);
                tmp /= 10;
            }
            reverse(digits.begin(), digits.end());
//             for(int i : digits) {
//                 cout << i << " ";

//             }
//             cout << "n";
            map<array<int, 4>, int> dp;
            auto dfs = [&] (auto &&dfs, int step, int upper, int mul, int add) -> int {
                if(step == digits.size()) {
                    // cout << step << " " << upper << " " << mul << " " << add << "n";
                    if(add == 0) return 0;
                    if(mul % add == 0) {
                        return 1;
                    } else {
                        return 0;
                    }
                }
                if(dp.count({step, upper, mul, add})) return dp[{step, upper, mul, add}];
                auto &ans = dp[{step, upper, mul, add}] = 0;
                for(int i = 0; i <= (upper ? digits[step] : 9); i++) {
                    ans += dfs(dfs, step + 1, upper & (i == digits[step]), (add == 0 && i == 0 ? mul : mul * i), add + i);
                }
                return ans;

            };
            return dfs(dfs, 0, 1, 1, 0);
        };
        // cout << query(9) << 'n';
        return query(r) - query(l - 1);
        return 1;
    }
};

