class Solution {
public:
    int solve(int N) {
        if (N == 0) return 0;
        string s = to_string(N);
        int M = s.size();
        auto &dp = pt, &dp2 = pt2;
        REP(i, M * 9 + 1) REP(j, M * 9 + 1) REP(k, i) dp[i][j][k] = 0;

        int eqsum = 0;
        ll eqprod = 1;

        REP(i, s.size()) {
            int d = s[i] - '0';
            REP(k, M * 9 + 1) REP(l, k + 1) REP(m, k) dp2[k][l][m] = 0;
            REP(j, 10) {
                REP1(k, 1, M * 9 + 1) REP(l, k + 1 - j) REP(m, k) {
                    dp2[k][l + j][m * j % k] += dp[k][l][m];
                }
                if (j < d && (i > 0 || j > 0)) {
                    REP1(k, 1, M * 9 + 1) {
                        if (eqsum + j <= k) dp2[k][eqsum + j][eqprod * j % k]++;
                    }
                }
                if (i > 0 && j > 0) {
                    REP1(k, 1, M * 9 + 1) {
                        if (j <= k) dp2[k][j][j % k]++;
                    }
                }
            }
            eqsum += d, eqprod *= d;
            swap(dp, dp2);
        }

        int ans = 0;
        REP1(i, 1, M * 9 + 1) ans += dp[i][i][0];
        ans += eqprod % eqsum == 0;

        return ans;
    }

    int beautifulNumbers(int l, int r) {
        return solve(r) - solve(l - 1);
    }
};

