int dfs(int pos, int sum, int mod, int s, bool num, bool lim) {
	if (pos == 0) {
		return num && sum == s && mod == 0;
	}
	if (num && !lim && ~dp[pos][sum][mod]) {
		return dp[pos][sum][mod];
	}
	int res = 0;
	if (!num) {
		res += dfs(pos - 1, 0, 0, s, false, false);
	}
	int upper = lim ? a[pos] : 9;
	for (int i = 1 - num; i <= upper; i ++) {
		int new_mod = (!num ? i : mod * i) % s;
		res += dfs(pos - 1, sum + i, new_mod, s, true, lim && (i == a[pos]));
	} 
	if (num && !lim) {
		dp[pos][sum][mod] = res;
	}
