class Solution {
public:
    long long r[10]={},pp[20]={},p10[20]={},p9[20]={};
    long long k;
    int dp(long long p, int s, int x, int c){
        if(c==0){
            if(p%s==0){
                return k;
            }
            return 0;
        }
        if(x>=10){
            return 0;
        }
        int res=0;
        long long np=p;
        int ns=s;
        for(int i=0;i<=c;++i){
            k/=pp[i];
            res+=dp(np,ns,x+1,c-i);
            k*=pp[i];
            np*=x;
            ns+=x;
        }
        return res;
    }
    int g(long long p, int s, int c){
        // cout<<"g "<<p<<" "<<s<<" "<<c<<endl;
        if(c==0){
            return p%s==0;
        }
        if(p==0){
            return p10[c];
        }
        k=pp[c];
        int res=p10[c]-p9[c];
        res+=dp(p,s,1,c);
        return res;
    }
    int f(int x){
        if(!x){
            return 0;
        }
        vector<int> a;
        int t=x;
        while(t){
            a.push_back(t%10);
            t/=10;
        }
        int n=a.size();
        int res=0;
        long long p=1;
        int s=0;
        for(int i=n-1;i>=0;--i){
            for(int j=1;j<a[i];++j){
                res+=g(p*j,s+j,i);
            }
            if(i!=n-1){
                if(a[i]){
                    res+=g(0,s,i);    
                }
                for(int j=1;j<=9;++j){
                    res+=g(j,j,i);
                }
            }
            p*=a[i];
            s+=a[i];
        }
        res+=p%s==0;
        // cout<<"f("<<x<<")="<<res<<endl;
        return res;
    }
    int beautifulNumbers(int l, int r) {
        pp[0]=1;
        p9[0]=1;
        p10[0]=1;
        for(int i=1;i<=10;++i){
            pp[i]=pp[i-1]*i;
            p9[i]=p9[i-1]*9;
            p10[i]=p10[i-1]*10;
        }
        // for(int i=l;i<=r;++i){
        //     f(i);
        // }
        return f(r)-f(l-1);
    }
};

