class Solution:  
    def beautifulNumbers(self, left: int, right: int) -> int:  
        def count_up_to(upper_bound: int) -> int:  
            digits = str(upper_bound)  
            length = len(digits)  

            @cache  
            def dp(position: int, is_tight: bool, has_started: bool,   
                   has_zero: bool, digit_sum: int, digit_product: int) -> int:  
                if position == length:  
                    if not has_started:
                        return 0  
                    if has_zero:
                        return 1 if digit_sum > 0 else 0  
                    return 1 if digit_sum > 0 and digit_product % digit_sum == 0 else 0  

                count = 0  
                max_digit = int(digits[position]) if is_tight else 9  

                for d in range(max_digit + 1):  
                    new_tight = is_tight and (d == max_digit)  

                    if not has_started:  
                        if d == 0:
                            count += dp(position + 1, new_tight, False, False, 0, 1)  
                        else:
                            count += dp(position + 1, new_tight, True, False, d, d)  
                    else:  
                        if d == 0:
                            count += dp(position + 1, new_tight, True, True, digit_sum, 0)  
                        else:  
                            if has_zero:
                                count += dp(position + 1, new_tight, True, True, digit_sum + d, 0)  
                            else:
                                count += dp(position + 1, new_tight, True, False,   
                                           digit_sum + d, digit_product * d)  

                return count  

            return dp(0, True, False, False, 0, 1)  

        return count_up_to(right) - count_up_to(left - 1)

