from functools import lru_cache
from bisect import bisect_left

def bs(a,x):
    l,r=0,len(a)-1
    while l<=r:
        m=(l+r)//2
        if a[m]==x:return m
        if a[m]<x:l=m+1
        else:r=m-1
    return -1

def st(a):
    n=len(a); s=1
    while s<n:s*=2
    t=[0]*(2*s)
    for i in range(n): t[s+i]=a[i]
    for i in range(s-1,0,-1): t[i]=t[2*i]+t[2*i+1]
    def q(l,r):
        l+=s; r+=s; tot=0
        while l<=r:
            if l&1:
                tot+=t[l]; l+=1
            if not(r&1):
                tot+=t[r]; r-=1
            l//=2; r//=2
        return tot
    return t,q

def gd(x):
    return list(map(int,str(x)))

def pf(x,full=False):
    if x==0:return (0,0,0,0) if not full else None
    t=x; c2=c3=c5=c7=0
    while t%2==0:
        c2+=1; t//=2
    while t%3==0:
        c3+=1; t//=3
    while t%5==0:
        c5+=1; t//=5
    while t%7==0:
        c7+=1; t//=7
    return (c2,c3,c5,c7) if (not full or t==1) else (-1,-1,-1,-1)
fd=[pf(d) for d in range(10)]
M2,M3,M5,M7=6,4,2,2
def cl(x,m):
    return x if x<=m else m
sF=[(-1,-1,-1,-1) if s==0 else pf(s,True) for s in range(82)]

@lru_cache(None)
def dp(p,L,tight,e2,e3,e5,e7,s,digs):
    if p==L:
        sx2,sx3,sx5,sx7=sF[s]
        return (1,1 if sx2!=-1 and e2>=sx2 and e3>=sx3 and e5>=sx5 and e7>=sx7 else 0)
    lim=digs[p] if (tight and digs) else 9; A,B=0,0
    for d in range(1,lim+1):
        nt=tight and (d==lim)
        ne2=cl(e2+fd[d][0],M2)
        ne3=cl(e3+fd[d][1],M3)
        ne5=cl(e5+fd[d][2],M5)
        ne7=cl(e7+fd[d][3],M7)
        ns=s+d
        if ns>81: break
        a,b=dp(p+1,L,nt,ne2,ne3,ne5,ne7,ns,digs if (tight and digs) else tuple([9]*L))
        A+=a; B+=b
    return (A,B)

def cnt(N):
    if N<1:return (0,0)
    digs=tuple(gd(N)); L=len(digs); A,B=0,0
    for l in range(1,L):
        a,b=dp(0,l,False,0,0,0,0,0,tuple([9]*l))
        A+=a; B+=b
    a,b=dp(0,L,True,0,0,0,0,0,tuple(digs))
    A+=a; B+=b
    return (A,B)

def sol(x):
    if x<1:return 0
    tot,beaut=cnt(x)
    return beaut+(x-tot)

class Solution:
    def beautifulNumbers(self, l:int, r:int) -> int:
        return sol(r)-sol(l-1)

