            #  0 
            if not is_num:
                res += dfs(i+1,s,False,is_num,tgt,t,mod)

            if not is_num:
                start = 1
            else:
                start = 0

            if is_limit:
                end = int(s[i])
            else:
                end = 9

            # 
            rest = len(s) - i
            if t + rest * 9 < tgt:
                return 0

            for d in range(start,end+1):
                # 
                if t + d > tgt:
                    break

                # limit
                if is_limit and d == end:
                    res += dfs(i+1,s,True,True,tgt,t+d,(mod*d) % tgt)
                else:
                    res += dfs(i+1,s,False,True,tgt,t+d,(mod*d) % tgt)

            return res

        res = 0
        r = str(r)
        l = str(l-1)
        for tgt in range(1,100):
            res += dfs(0,r,True,False,tgt,0,1) - dfs(0,l,True,False,tgt,0,1)

        dfs.cache_clear()
        return res

