using ll=long long;
class Solution {
public:
	struct ST{
		int pos,tight,st,f,s;ll p;
		bool operator==(const ST& other)const{
			return pos==other.pos&&tight==other.tight&&st==other.st&&
				f==other.f&&s==other.s&&p==other.p;
		}
	};
	struct Hash{
		size_t operator()(const ST& s)const{
			size_t res=17;
			res=res*31+std::hash<int>()(s.pos);
			res=res*31+std::hash<int>()(s.tight);
			res=res*31+std::hash<int>()(s.st);
			res=res*31+std::hash<int>()(s.f);
			res=res*31+std::hash<int>()(s.s);
			res=res*31+std::hash<ll>()(s.p);
			return res;
		}
	};
	unordered_map<ST,ll,Hash>mp;
	ll dp(int pos,bool tight,bool st,bool f,int s,ll p,const vector<int>&digits){
		if(pos==digits.size()){
			if(!st)return 0;
			if(f)return 1;
			return (p%s==0)?1LL:0LL;
		}
		ST cur={pos,tight,st,f,s,p};
		if(mp.find(cur)!=mp.end())return mp[cur];
		ll c=0;
		int lim=tight?digits[pos]:9;
		for(int i=0;i<=lim;i++){
			bool newTight=tight&&(i==lim);
			if(!st){
				if(i==0)c+=dp(pos+1,newTight,false,false,0,1,digits);
				else c+=dp(pos+1,newTight,true,false,i,(ll)i,digits);
			}else{
				int newSum=s+i;bool newZeroFound=f||(i==0);
				ll newProd=(f||i==0)?0LL:p*i;
				c+=dp(pos+1,newTight,true,newZeroFound,newSum,newProd,digits);
			}
		}mp[cur]=c;
		return c;
	}
	ll cal(ll x){
		if(x<1)return 0;vector<int>digits;
		while(x>0){
			digits.push_back(static_cast<int>(x%10));x/=10;
		}reverse(digits.begin(),digits.end());mp.clear();
		return dp(0,true,false,false,0,1,digits);
	}
	int beautifulNumbers(int l,int r){
		ll cnt=cal(r)-cal(l-1);
		return static_cast<int>(cnt);
	}
};

